import io

from flask import (
    Flask
)

from flask import request, render_template
import base64

# Create the application instance
from src.classes.inference.Sampler import Sampler

app = Flask(__name__, template_folder="templates")


@app.route('/api/predict')
def home():
    """
    This function just responds to the browser ULR
    localhost:5000/

    :return:        the rendered template 'home.html'
    """

    image_path = "../../data/prod/mockup.png"
    html_path = "../../data/prod/mockup.html"

    with open(image_path, "wb") as fh:
        # fh.write(base64.decodebytes(request.args.get('image_data')))
        fh.write(base64.b64decode(""))

    sampler = Sampler(model_json_path="../../bin/model_json.json",
                      model_weights_path="../../bin/weights.h5")
    sampler.convert_single_image(html_path, png_path=image_path, print_generated_output=False,
                                 get_sentence_bleu=False, original_gui_filepath=None,
                                 style="default")

    return render_template(html_path)


# If we're running in stand alone mode, run the application
if __name__ == '__main__':
    app.run(debug=True)
